import React from 'react';
import {useState} from 'react';



function decrece(){
    setCount(count - 1)
}

function crece(){
    setCount(count + 1)
}

const ItemCount = () => {

    const [count,setCount] = useState(5)

    return (
    <div class="cuadro">
        <p>Guitarra Gibson</p>
        <div class="botonesCuadro">
            <button onClick={decrece}>
            -
            </button>
                <span>{count}</span>
            <button onClick={crece}>
            +
            </button>
        </div>
    </div>

    );
}



export default ItemCount;
