import React from 'react';
import {useState} from 'react';





const ItemCount = (stock,initial,addOn) => {

    if(count <= parseInt(stock) && count>parseInt(initial)){
    const [count,setCount] = useState(5);

    function decrece(){
        setCount(count - 1)
    };

    function crece(){
        setCount(count + 1)
    };
}

    return (
    <div class="cuadro">
        <p>Guitarra Gibson</p>
        <div class="botonesCuadro">
            <button onClick={decrece}>
            -
            </button>
                <span>{count}</span>
            <button onClick={crece}>
            +
            </button>
        </div>
    </div>

    );
}



export default ItemCount;
