import React from 'react';
import {useState} from 'react';

const ItemCount = (stock,initial,onAdd) => {

    let [count,setCount] = useState(initial);

    function crece(){
        if(count < stock){
            setCount(count + 1);
        }
    };

    function decrece(){
        if(count > 1){
            setCount(count - 1);
        };
    }

    function confirmar(){
        console.log("Has realizado tu pedido.")
    };

    return (
    <div class="cuadro">
        <p>Guitarra Gibson</p>
        <div class="botonesCuadro">
            <button onClick={decrece}>
            -
            </button>
                <span>{count}</span>
            <button onClick={crece}>
            +
            </button>

        </div>
        <button onClick={confirmar}>
            Confirmar
            </button>
    </div>

    );
}


export default ItemCount;
