function ItemCount() {
    const [contador,setContador] = useState();
    function cambiarContador(){
        contador = contador + 1
        setContador(contador + 1);
    }
    return (
<div>
    <div class="product-quantity">
        <button onClick={()=> setContador('hola')}>+</button>
    {/* <input type="number" value="2" min="1">{contador}</input> */}
    </div>
        {/* <ItemCount stock = parseInt("5") initial=parseInt("1") onAdd= /> */}

</div>
    )
}


export default ItemCount;

